import React from "react";
import Button from "@material-ui/core/Button";
import AddIcon from "@material-ui/icons/Add";
import { useHistory } from 'react-router-dom';


const containerStyle = {

  // backgroundColor: "#cccccc",
  // color: "#000000",
  // display: "flex",
  // alignItems: "center",
  // fontWeight: "bold",
  // fontSize: "1.5em",
  // justifyContent: "space-around",
  // padding: "0.5em 1em",
  // width: "100%"
};


export default props => {
  const history = useHistory()
  function home() {
    //  e.preventDefault();
    history.push(`/`);
    window.location.reload(false);
  }
  return (
    <div  id="titlebar">
      <div>Meeting Scheduler</div>
      <Button
       style={{backgroundColor:'#3a7afe',color:"#ffffff"}}
        startIcon={<AddIcon />}
        onClick={props.onShowFormClick}
        title={props.formVisible ? `Hide Form` : `Show Form`}
        disabled={props.formVisible}

      >
        Create meeting
      </Button>&nbsp;&nbsp;
      <Button
       style={{backgroundColor: "#29dd0f",color:"#ffffff"}}
        onClick={home}
        title={props.formVisible ? `Hide Form` : `Show Form`}
        disabled={props.formVisible}

      >
        Home Page
      </Button>
    </div>
  );
};
